import { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './signUp.css';
import img1 from '../image/6207670.jpg';
import { Link } from 'react-router-dom';

const SignUp = () => {
    const [user, setUser] = useState([]);
    const [email, setEmail] = useState('');
    // const [username, setUsername] = useState('');
    // const [usernameError, setUsernameError] = useState('');
    const [password, setPassword] = useState('');
    const [FullName, setFullName] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [FullNameError, setFullNameError] = useState('');
    useEffect(() => {
        fetchUser();
    }, []);
    const fetchUser = async () => {
        try {
            const response = await axios.get(`http://localhost:3000/users`);
            setUser(response.data);
            console.log(response.data);
        } catch (err) {
            console.error('Error fetching user: ', err);
        }
    };


    const handleRegisterUser = (user) => {
        axios
            .post(`http://localhost:3000/signUp`, user)
            .then(() => {
                alert('User registration sucessfully');
                fetchUser();
            })
            .catch((err) => console.log(err));
    };


    const validateForm = () => {
        let isValid = true;

        if (!email) {
            alert(setEmailError('Please enter an email'));
            isValid = false;
        } else if (user.some((u) => u.email === email)) {
            setEmailError('Email already exists. Please choose a different email');
            isValid = false;
        }

        if (!confirmPassword) {
            setPasswordError('Please confirm your password');
            isValid = false;
        } else if (password !== confirmPassword) {
            setPasswordError('Passwords do not match');
            isValid = false;
        }

        if (!password) {
            setPasswordError('Please enter a password');
            isValid = false;
        }

        if (!FullName) {
            setFullNameError('Please enter your name');
            isValid = false;
        }
        return isValid;
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (validateForm()) {
            const userData = {
                name: FullName,
                email,
                password,
                role: 'user',
            };
            handleRegisterUser(userData);
            console.log('User registered successfully');
            // Reset the form fields after successful submission
            setEmail('');
            setPassword('');
            setFullName('');
        }
    };
    return (
        <div class="Desktop3">
            <div class="img"><img src={img1} height="600px" width="900px" /></div>
            <div class="Login">
                <div class="text">
                    <h2>SIGN UP</h2>
                </div>
                <div class="form">
                    <form onSubmit={handleSubmit}>
                        <label for="ID"><b>Email</b></label>
                        <input
                            name="ID"
                            placeholder="Your Email"
                            type="text"
                            required
                            value={email}
                            onChange={(e) => {
                                setEmail(e.target.value);
                                setEmailError('');
                            }}
                        ></input>
                        {emailError && <p>{emailError}</p>}

                        <label for="uname"><b>Your Name</b></label>
                        <input
                            placeholder="Full name"
                            type="text"
                            required
                            value={FullName}
                            onChange={(e) => {
                                setFullName(e.target.value);
                                setFullNameError('');
                            }}
                        ></input>
                        {FullNameError && <p>{FullNameError}</p>}

                        <label for="psw"><b>Password</b></label>
                        <input
                            placeholder="Enter password"
                            type="password"
                            required
                            value={password}
                            onChange={(e) => {
                                setPassword(e.target.value);
                                setPasswordError('');
                            }}
                        ></input>
                        {passwordError && <p>{passwordError}</p>}

                        <label for="psw"><b>Confirm Password</b></label>
                        <input
                            placeholder="Confirm password"
                            type="password"
                            required
                            value={confirmPassword}
                            onChange={(e) => {
                                setConfirmPassword(e.target.value);
                                setPasswordError('');
                            }}
                        ></input>
                        {passwordError && <p>{passwordError}</p>}

                        <button type="submit" onClick={handleSubmit}>
                            Sign up
                        </button>
                        <Link to="/signup">Do not have an account?</Link>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default SignUp;
