import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import styles from './projectPage.css';
import img2 from "../image/icons8-sign-in-64.png"
const ProjectPage = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      // Fetch data from the API
      fetch("http://localhost:3000/cl/projects")
        .then((response) => response.json())
        .then((data) => {
          setProjects(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching data: " + error);
          setLoading(false);
        });
    }, []);
  
    // Function to get the CSS class for each status
    const getStatusClass = (status) => {
      switch (status) {
        case "approved":
          return "status-accepted";
        case "waiting":
          return "status-waiting";
        case "rejected":
          return "status-rejected";
        default:
          return "";
      }
    };
  
    // Function to navigate to the project page with the given project ID
    const navigateToProjectPage = (projectID) => {
      window.location.href = `../form/index.html?projectID=${projectID}`;
    };

  return (
    <div>
 <header class="header">
        <div class="navbar ">
          <ul class="navbar-list">
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/homepageCL">Home</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/projects">Projects</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/applicatants">Applicatiants</Link>
              </a>
            </li>

          </ul>

          <div class="user">
            <div class="user-img">
              <span class="user-H">
                Q
              </span>
            </div>
            <div class="user-name">
              Minh Quyen
            </div>
          </div>
        </div>
      </header>

    <section class="centent-body">
        <div class="text">
            <h2>PROJECT</h2>
        </div>
        <div class="listProject">
                <a  class="CreateProject">
                    <Link to="/createProject">
                    <p>Create Project 
                    </p>
                    <img src={img2} class="textAlign" />
                    </Link>
                </a>
        </div>

        <div class ="listMyExistingProj">

        </div>

    </section>

    </div>
  )
}

export default ProjectPage
