import { useState, useEffect } from 'react';
import axios from 'axios';
// import Radio from '@mui/material/Radio';
// import RadioGroup from '@mui/material/RadioGroup';
// import FormControlLabel from '@mui/material/FormControlLabel';
// import FormControl from '@mui/material/FormControl';
// import FormLabel from '@mui/material/FormLabel';

const SignUp = () => {
    const [user, setUser] = useState([]);
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [FullName, setFullName] = useState('');
    const [birthdate, setBirthdate] = useState('');
    const [emailError, setEmailError] = useState('');
    const [usernameError, setUsernameError] = useState('');

    const [passwordError, setPasswordError] = useState('');
    const [FullNameError, setFullNameError] = useState('');
    const [birthdateError, setBirthdateError] = useState('');

    useEffect(() => {
        fetchUser();
    }, []);

    const fetchUser = async () => {
        try {
            const response = await axios.get(`http://localhost:3000/users`);
            setUser(response.data);
            console.log(response.data);
        } catch (err) {
            console.error('Error fetching user: ', err);
        }
    };

    const handleRegisterUser = (user) => {
        axios
            .post(`http://localhost:3000/users`, user)
            .then(() => {
                fetchUser();
            })
            .catch((err) => console.log(err));
    };


    const validateForm = () => {
        let isValid = true;

        if (!email) {
            setEmailError('Please enter an email');
            isValid = false;
        } else if (user.some((u) => u.email === email)) {
            setEmailError('Email already exists. Please choose a different email');
            isValid = false;
        }

        if (!username) {
            setUsernameError('Please enter a username');
            isValid = false;
        } else if (user.some((u) => u.username === username && u.email !== email)) {
            setUsernameError('Username already exists. Please choose a different username');
            isValid = false;
        }

        if (!password) {
            setPasswordError('Please enter a password');
            isValid = false;
        }

        if (!FullName) {
            setFullNameError('Please enter your name');
            isValid = false;
        }



        if (!birthdate) {
            setBirthdateError('Please enter your birthdate');
            isValid = false;
        }

        return isValid;
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (validateForm()) {
            const userData = {
                email,
                username,
                password,
                FullName,
                birthdate,
                role: 'user',
            };

            handleRegisterUser(userData);
            console.log(userData);

            setEmail('');
            setUsername('');
            setPassword('');
            setFullName('');
            setBirthdate('');
        }
    };

    return (
        <div>
            <div></div>
            <div>
                <div>
                    <div>
                        <form onSubmit={handleSubmit}>
                            <h1>Sign up</h1>
                            <div>
                                <input
                                    placeholder="Enter email"
                                    type="email"
                                    required
                                    value={email}
                                    onChange={(e) => {
                                        setEmail(e.target.value);
                                        setEmailError('');
                                    }}
                                ></input>
                                {emailError && <p>{emailError}</p>}
                            </div>
                            <div>
                                <div>
                                    <input
                                        placeholder="Full name"
                                        type="text"
                                        required
                                        value={FullName}
                                        onChange={(e) => {
                                            setFullName(e.target.value);
                                            setFullNameError('');
                                        }}
                                    ></input>
                                    {FullNameError && <p>{FullNameError}</p>}
                                </div>

                            </div>
                            <div>
                                <input
                                    placeholder="Enter username"
                                    type="text"
                                    required
                                    value={username}
                                    onChange={(e) => {
                                        setUsername(e.target.value);
                                        setUsernameError('');
                                    }}
                                ></input>
                                {usernameError && <p>{usernameError}</p>}
                            </div>

                            <div>
                                <input
                                    placeholder="Enter password"
                                    type="password"
                                    required
                                    value={password}
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                        setPasswordError('');
                                    }}
                                ></input>
                                {passwordError && <p>{passwordError}</p>}
                            </div>

                            <div>
                                <input
                                    placeholder="Confirm password"
                                    type="password"
                                    required
                                    value={confirmPassword}
                                    onChange={(e) => {
                                        setConfirmPassword(e.target.value);
                                        setPasswordError('');
                                    }}
                                ></input>
                                {passwordError && <p>{passwordError}</p>}
                            </div>
                            <div>
                                <input
                                    placeholder="Date of Birth"
                                    type="date"
                                    required
                                    value={birthdate}
                                    onChange={(e) => {
                                        setBirthdate(e.target.value);
                                        setBirthdateError('');
                                    }}
                                ></input>
                                {birthdateError && <p>{birthdateError}</p>}
                            </div>
                            <button type="submit" onClick={handleSubmit}>
                                Sign up
                            </button>
                            <a href="/Login">Sign In ?</a>
                        </form>
                    </div>
                    <div>
                        <div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SignUp;
