import React from 'react'
import { Link } from 'react-router-dom'
import styles from './createProject.css';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate from react-router
import axios from 'axios';

const ProjectCL = () => {
  const [projectName, setProjectName] = useState('');
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [address, setAddress] = useState('');
  const [abilityList, setAbilityList] = useState('');

  const handleProjectNameChange = (e) => {
    setProjectName(e.target.value);
  };

  const handleImageUpload = (e) => {
    // You can handle file uploads here
    setImage(e.target.files[0]);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleQuantityChange = (e) => {
    setQuantity(e.target.value);
  };

  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

  const handleAddressChange = (e) => {
    setAddress(e.target.value);
  };

  const handleAbilityListChange = (e) => {
    setAbilityList(e.target.value);
  };
  
  useEffect(() => {
    if (projectName.length < 3) {
      console.log('Project name should be at least 3 characters long.');
    }
  }, [projectName]);

  // useEffect to validate the start and end date
  useEffect(() => {
    if (startDate && endDate && startDate > endDate) {
      console.log('End date should be after the start date.');
    }
  }, [startDate, endDate]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can access the values you've stored in state here
    console.log('Project Name:', projectName);
    console.log('Image:', image);
    console.log('Description:', description);
    console.log('Quantity:', quantity);
    console.log('Start Date:', startDate);
    console.log('End Date:', endDate);
    console.log('Address:', address);
    console.log('Ability List:', abilityList);

    // You can perform any other actions here, such as sending data to a server
  };

  return (
    <div>
      <header class="header">
        <div class="navbar ">
          <ul class="navbar-list">
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/homepageCL">Home</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/projects">Projects</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/applicatants">Applicatiants</Link>
              </a>
            </li>

          </ul>

          <div class="user">
            <div class="user-img">
              <span class="user-H">
                Q
              </span>
            </div>
            <div class="user-name">
              Minh Quyen
            </div>
          </div>
        </div>
      </header>

      <section class="centent-body">
        <div class = "text">
            <h2>CREATE PROJECT</h2>
        </div>
        <form onSubmit={handleSubmit} class="form">
            <label for="uname"><b>Project's Name</b></label>
            <input type="text" placeholder="Enter Project's Name" name="uname" required/>

            <label for="imageUpload">Select an Image:</label>
            <input type="file" id="imageUpload" name="imageUpload" />
            <input type="submit" value="Upload"/>

            <label>Message</label>
            <textarea cols="90" rows="5" placeholder="Description"></textarea>

            <label for="quan"><b>Quantity</b></label>
            <input type="password" placeholder="Enter the number of participants" name="quan" required/>

            <label for="dateS">Start Date</label>
            <input type="date" id="dateInput" name="dateInput" />

            <label for="dateE">End Date</label>
            <input type="date" id="dateInput" name="dateInput"/>

            <label for="Add">Address</label>
            <input type="text" placeholder="Enter Address" name="Add" required/>

            <label for="Abili">Ability List</label>
            <textarea cols="90" rows="5" placeholder="Description"></textarea>            

            <button type="submit">SUBMIT</button>

        </form>
    </section>

    <footer class="footer">
        <div class="footer-content-1">
            <div class="footer-content-11">
                <div class="useful-link">
                    <span>
                        Useful Links
                    </span>
                    <ul class="useful-link-list">
                        <li>
                            Home
                        </li>
                        <li>
                            About us
                        </li>
                        <li>
                            Product
                        </li>
                        <li>
                            Services
                        </li>
                        <li>
                            Contact us
                        </li>

                    </ul>
                </div>
                <div class="about-us">
                    <span>
                        About us
                    </span>
                    <p>
                        Green Summer
                    </p>

                </div>
                <div class="Connect-with-us">
                    <span>

                        Connect with us
                    </span>
                    <ul>
                        <li>
                            <i class="fa-regular fa-comments"></i>
                            Contact us
                        </li>
                        <li>
                            minhquyen123@grad.uit.edu.vn
                        </li>
                        <li>
                            0123456789
                        </li>

                    </ul>
                </div>
            </div>

        </div>
        <div class="footer-content-2">
            <span>
                Copyright © Company name
            </span>
        </div>
    </footer>
    </div>
  )
}

export default ProjectCL
