import React from 'react'
import { Link } from 'react-router-dom'
import styles from './createProject.css';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate from react-router

const ProjectCL = () => {
  const [user, setUser] = useState([]);
  const [email, setemail] = useState('');
  const [password, setPassword] = useState('');
  const [hasEmailError, setHasEmailError] = useState(false);
  const [hasPasswordError, setHasPasswordError] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  useEffect(() => {
    fetchUser();
    // Check local storage for "rememberMe" flag and email
    const storedRememberMe = localStorage.getItem('rememberMe');
    const storedEmail = localStorage.getItem('email');
    if (storedRememberMe === 'true' && storedEmail) {
      setRememberMe(true);
      setemail(storedEmail);
    }
  }, []);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/users`);
      setUser(response.data);
      console.log(response.data);
    } catch (err) {
      console.error('Error fetching user: ', err);
    }
  };

  const handleMailChange = (e) => {
    setemail(e.target.value);
    setHasEmailError(false);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setHasPasswordError(false);
  };

  const validateForm = () => {
    let isValid = true;

    if (!email) {
      setHasEmailError(true);
      isValid = false;
    }

    if (!password) {
      setHasPasswordError(true);
      isValid = false;
    }

    return isValid;
  };

  const navigate = useNavigate(); // Initialize the navigate function

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const foundUser = user.find((u) => u.email === email);
      console.log('foundUser:', foundUser);

      if (foundUser) {
        if (foundUser.password && foundUser.password === password) {
          if (foundUser.role === 'admin') {
            // User is an admin, redirect to the admin page
            console.log('password:', password);
            console.log('role:', foundUser.role);
            // Add an alert for successful login
            alert("Login successfully as an admin");
          } else if (foundUser.role === 'leaders') {
            // User is a regular user, redirect to the home page
            console.log('email:', email);
            console.log('password:', password);
            console.log('role:', foundUser.role);
            // Add an alert for successful login
            alert("Login successfully as a community leader");
            navigate('/homepageCL');

          }
          else {
            console.log('email:', email);
            console.log('password:', password);
            console.log('role:', foundUser.role);
            // Redirect to the "/homepageUser" route
          }

          // Store email and "rememberMe" flag in local storage if "Remember me" is checked
          if (rememberMe) {
            localStorage.setItem('email', email);
            localStorage.setItem('rememberMe', 'true');
          } else {
            // Clear stored email and "rememberMe" flag
            localStorage.removeItem('email');
            localStorage.removeItem('rememberMe');
          }
        } else {
          // Invalid password
          console.log('Invalid password');
          // Add an alert for an invalid password
          alert("Invalid password");
        }
      } else {
        // User not found
        alert("User not found");
      }
    }
  };
  return (
    <div>
      <header class="header">
        <div class="navbar ">
          <ul class="navbar-list">
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/homepageCL">Home</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/projects">Projects</Link>
              </a>
            </li>
            <li class="navbar-item">
              <a class="white-text">
                <Link to="/applicatants">Applicatiants</Link>
              </a>
            </li>

          </ul>

          <div class="user">
            <div class="user-img">
              <span class="user-H">
                Q
              </span>
            </div>
            <div class="user-name">
              Minh Quyen
            </div>
          </div>
        </div>
      </header>

      <section class="centent-body">
        <div class = "text">
            <h2>CREATE PROJECT</h2>
        </div>
        <form onSubmit={handleSubmit} class="form">
            <label for="ID"><b>ID</b></label>
            <input type="text" placeholder="Enter Project'ID" name="ID" required/>

            <label for="uname"><b>Project's Name</b></label>
            <input type="text" placeholder="Enter Project's Name" name="uname" required/>

            <label for="imageUpload">Select an Image:</label>
            <input type="file" id="imageUpload" name="imageUpload" />
            <input type="submit" value="Upload"/>

            <label>Message</label>
            <textarea cols="90" rows="5" placeholder="Description"></textarea>

            <label for="quan"><b>Quantity</b></label>
            <input type="password" placeholder="Enter the number of participants" name="quan" required/>

            <label for="dateS">Start Date</label>
            <input type="date" id="dateInput" name="dateInput" />

            <label for="dateE">End Date</label>
            <input type="date" id="dateInput" name="dateInput"/>

            <label for="Add">Address</label>
            <input type="text" placeholder="Enter Address" name="Add" required/>

            <label for="Abili">Ability List</label>
            <textarea cols="90" rows="5" placeholder="Description"></textarea>            

            <button type="submit">SUBMIT</button>

        </form>
    </section>

    <footer class="footer">
        <div class="footer-content-1">
            <div class="footer-content-11">
                <div class="useful-link">
                    <span>
                        Useful Links
                    </span>
                    <ul class="useful-link-list">
                        <li>
                            Home
                        </li>
                        <li>
                            About us
                        </li>
                        <li>
                            Product
                        </li>
                        <li>
                            Services
                        </li>
                        <li>
                            Contact us
                        </li>

                    </ul>
                </div>
                <div class="about-us">
                    <span>
                        About us
                    </span>
                    <p>
                        Green Summer
                    </p>

                </div>
