import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import styles from './projectPage.css';
import img2 from "../image/icons8-sign-in-64.png"

const ProjectPage = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch data from the API
        fetch("http://localhost:3000/projects")
            .then((response) => response.json())
            .then((data) => {
                setProjects(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Error fetching data: " + error);
                setLoading(false);
            });
    }, []);


   // Function to get the CSS class for each status
   const getStatusClass = (status) => {
    switch (status) {
        case "approved":
            return "status-accepted";
        case "waiting":
            return "status-waiting";
        case "rejected":
            return "status-rejected";
        default:
            return "";
    }
};

    const getTheProjectID = (projectID) => {
        return (
            <Link to={`/projects/${projectID}`}>View Project Details</Link>
        );
    };



    return (
        <div>
            <section class="centent-body">
                <div class="text">
                    <h2>PROJECT</h2>
                </div>

                <div class="listProject">
                    <a class="CreateProject">
                        <Link to="/createProject">
                            Create Project
                        </Link>
                    </a>
                </div>

                <div class="listMyExistingProj">
                    {loading ? (
                        <p>Loading...</p>
                    ) : (
                        projects.length > 0 ? (
                            <table>
                                <thead>
                                    <tr>
                                        <th>ProjectID</th>
                                        <th>Project Name</th>
                                        <th>Quantity</th>
                                        <th>Start Date</th>
                                        <th>End Date</th>
                                        <th>Address</th>
                                        <th>Ability List</th>
                                        <th>Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {projects.map((project) => (
                                        <tr key={project.projectID} onClick={() => getTheProjectID(project.projectID)}>
                                            <td>{project.projectID}</td>
                                            <td>{project.projectName}</td>
                                            <td>{project.quantity}</td>
                                            <td>{project.startDate}</td>
                                            <td>{project.endDate}</td>
                                            <td>{project.address}</td>
                                            <td>{project.abilityList}</td>
                                            <td className={getStatusClass(project.status)}>{project.status}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p>No projects found.</p>
                        )
                    )}
                </div>

            </section>
        </div>
    )
}

export default ProjectPage;
