import { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './signUp.css';
import img1 from './image/6207670.jpg';
// import img2 from '/image/6207670.jpg';
// import img3 from '/image/6207670.jpg';

const SignUp = () => {
    const [user, setUser] = useState([]);
    const [email, setEmail] = useState('');
    // const [username, setUsername] = useState('');
    // const [usernameError, setUsernameError] = useState('');

    const [password, setPassword] = useState('');
    const [FullName, setFullName] = useState('');
    const [emailError, setEmailError] = useState('');

    const [passwordError, setPasswordError] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const [FullNameError, setFullNameError] = useState('');

    useEffect(() => {
        fetchUser();
    }, []);

    const fetchUser = async () => {
        try {
            const response = await axios.get(`http://localhost:3000/users`);
            setUser(response.data);
            console.log(response.data);
        } catch (err) {
            console.error('Error fetching user: ', err);
        }
    };


    const handleRegisterUser = (user) => {
        axios
            .post(`http://localhost:3000/signUp`, user)
            .then(() => {
                fetchUser();
            })
            .catch((err) => console.log(err));
    };


    const validateForm = () => {
        let isValid = true;

        if (!email) {
            setEmailError('Please enter an email');
            isValid = false;
        } else if (user.some((u) => u.email === email)) {
            setEmailError('Email already exists. Please choose a different email');
            isValid = false;
        }

        if (!confirmPassword) {
            setPasswordError('Please confirm your password');
            isValid = false;
        } else if (password !== confirmPassword) {
            setPasswordError('Passwords do not match');
            isValid = false;
        }

        if (!password) {
            setPasswordError('Please enter a password');
            isValid = false;
        }

        if (!FullName) {
            setFullNameError('Please enter your name');
            isValid = false;
        }
        return isValid;
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (validateForm()) {
            const userData = {
                name: FullName,
                email,
                password,
                role: 'user',
            };
            alert("hello");

            handleRegisterUser(userData);
            console.log('User registered successfully');

            // Reset the form fields after successful submission
            setEmail('');
            // setUsername('');
            setPassword('');
            setFullName('');
        }
    };


    return (
        // <div>
        //     <div>
        //         <div>
        //             <div>
        //                 <form onSubmit={handleSubmit}>
        //                     <h1>Sign up</h1>
        //         <div>
        //             <div>
        //                 <input
        //                     placeholder="Full name"
        //                     type="text"
        //                     required
        //                     value={FullName}
        //                     onChange={(e) => {
        //                         setFullName(e.target.value);
        //                         setFullNameError('');
        //                     }}
        //                 ></input>
        //                 {FullNameError && <p>{FullNameError}</p>}
        //             </div>

        //         </div>
        //         <div>
        //             <input
        //                 placeholder="Enter email"
        //                 type="email"
        //                 required
        //                 value={email}
        //                 onChange={(e) => {
        //                     setEmail(e.target.value);
        //                     setEmailError('');
        //                 }}
        //             ></input>
        //             {emailError && <p>{emailError}</p>}
        //         </div>

        //         <div>
        //             <input
        //                 placeholder="Enter password"
        //                 type="password"
        //                 required
        //                 value={password}
        //                 onChange={(e) => {
        //                     setPassword(e.target.value);
        //                     setPasswordError('');
        //                 }}
        //             ></input>
        //             {passwordError && <p>{passwordError}</p>}
        //         </div>

        //         <div>
        //             <input
        //                 placeholder="Confirm password"
        //                 type="password"
        //                 required
        //                 value={confirmPassword}
        //                 onChange={(e) => {
        //                     setConfirmPassword(e.target.value);
        //                     setPasswordError('');
        //                 }}
        //             ></input>
        //             {passwordError && <p>{passwordError}</p>}
        //         </div>

        //         <button type="submit" onClick={handleSubmit}>
        //             Sign up
        //         </button>
        //         <a href="/Login">Sign In ?</a>
        //     </form>
        // </div>

        //         </div>
        //     </div>
        // </div>
        <div class="Desktop3">
            <div class="img"><img src={img1} height="600px" width="900px" /></div>

            <div class="Login">
                <div class="text">
                    <h2>SIGN UP</h2>
                </div>
                <div class="form">
                    <form onSubmit={handleSubmit}>
                        <label for="ID"><b>Email</b></label>
                        <div>
                            <input
                                placeholder="Your name"
                                type="text"
                                required
                                value={FullName}
                                onChange={(e) => {
                                    setFullName(e.target.value);
                                    setFullNameError('');
                                }}
                            ></input>
                            {FullNameError && <p>{FullNameError}</p>}
                        </div>

                        <label for="uname"><b>Your Name</b></label>
                        <input type="text" placeholder="Enter Your Name" name="uname" required />

                        <label for="psw"><b>Password</b></label>
                        <input type="password" placeholder="Enter Password" name="psw" required />

                        <label for="psw"><b>Confirm Password</b></label>
                        <input type="password" placeholder="Enter Password Again" name="psw" required />

                        <div class="regis">
                            <p class="regis"><i><a class="regis" href="/login/index.html">Log in</a></i></p>
                        </div>

                        <button type="submit" onClick={handleSubmit}>
                            Sign up
                        </button>
                        <a href="/Login">Sign In ?</a>
                    </form>
                </div>
            </div>


        </div>
    );
}

export default SignUp;
