import { useState, useEffect } from 'react';
import axios from 'axios';
import img1 from '../image/6207670.jpg';

function Login() {
  const [user, setUser] = useState([]);
  const [email, setemail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setemailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  useEffect(() => {
    fetchUser();
  }, []);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/users`);
      setUser(response.data);
      console.log(response.data);
    } catch (err) {
      console.error('Error fetching user: ', err);
    }
  };

  const handleMailChange = (e) => {
    setemail(e.target.value);
    setemailError('');
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setPasswordError('');
  };

  const validateForm = () => {
    let isValid = true;

    if (!email) {
      setemailError('Please enter a email');
      isValid = false;
    }

    if (!password) {
      setPasswordError('Please enter a password');
      isValid = false;
    }

    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const foundUser = user.find((u) => u.email === email);
      console.log('foundUser:', foundUser);

      if (foundUser) {
        if (foundUser.password && foundUser.password === password) {
          if (foundUser.role === 'admin') {
            // User is an admin, redirect to the admin page
            console.log('password:', password);
            console.log('role:', foundUser.role);

          } else if (foundUser.role === 'leaders') {
            // User is a regular user, redirect to the home page
            console.log('email:', email);
            console.log('password:', password);
            console.log('role:', foundUser.role);

          }
        } else {
          // Invalid password
          console.log('Invalid password');
        }
      } else {
        // User not found
        alert("User not found");

      }
    }
  };

  return (
    <div class="Desktop3">
      <div class="img"><img src={img1} height="600px" width="900px" /></div>

      <div class="Login">
        <div class="text">
          <h2>LOGIN</h2>
        </div>
        <div class="form">
          <label for="uname"><b>User Name</b></label>
          <input
                  placeholder="Enter email"
                  type="text"
                  id="email"
                  value={email}
                  onChange={handleMailChange}
                  required
                />
                {emailError && <p>{emailError}</p>}

          <label for="psw"><b>Password</b></label>
          <input
                  placeholder="Enter password"
                  type="password"
                  id="password"
                  value={password}
                  onChange={handlePasswordChange}
                  required
                />
                {passwordError && <p>{passwordError}</p>}

          <div class="regis">
            <p class="regis"><i><a class="regis" href="/signup/index.html">Do not have account?</a></i></p>
          </div>

          <button type="submit" onClick={handleSubmit}>
                Sign in
            </button>
          <label class="check">
            <input type="checkbox" checked="checked" name="remember" /> Remember me
          </label>

        </div>
      </div>
    </div>  
    );
    {/*
     <div>
      <div ></div>
      <div >
        <div>
          <div>
            <form onSubmit={handleSubmit}>
              <h1>Sign in to your account</h1>
              <div>
                <input
                  placeholder="Enter email"
                  type="text"
                  id="email"
                  value={email}
                  onChange={handleMailChange}
                  required
                />
                {emailError && <p>{emailError}</p>}
              </div>
              <div>
                <input
                  placeholder="Enter password"
                  type="password"
                  id="password"
                  value={password}
                  onChange={handlePasswordChange}
                  required
                />
                {passwordError && <p>{passwordError}</p>}
              </div>
              <button type="submit" onClick={handleSubmit}>
                Sign in
              </button>

              <p>
                No account?
              </p>
              <p>Forget your password?</p>
            </form>
          </div>
          <div>
            <div>
            </div>
          </div>
        </div>
      </div>
    </div> 
  */}
}

export default Login;
