import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import styles from './projectPage.css';

const ProjectDetails = () => {
    const { projectID } = useParams();
    const [projectData, setProjectData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch project data based on the projectID
        fetch(`http://localhost:3000/projects/${projectID}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json();
            })
            .then((data) => {
                setProjectData(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Error fetching data: " + error);
                setLoading(false);
            });
    }, [projectID]);

    const handleDeleteProject = () => {
        // Implement the logic to delete the project here
        // You can make a DELETE request to the server to delete the project
        // After successful deletion, you can navigate back to the ProjectPage
        // Example: fetch(`http://localhost:3000/projects/${projectID}`, { method: 'DELETE' }).then(/* Handle success */);
    };

    return (
        <div>
            {loading ? (
                <p>Loading...</p>
            ) : projectData ? (
                <section className='text'>
                    <h2>Project Details</h2>
                    <p>Project ID: {projectID}</p>
                    <p>Project Name: {projectData.projectName}</p>
                    <p>Project description: {projectData.description}</p>
                    <p>Project address: {projectData.address}</p>
                    <p>Project start date: {projectData.startDate}</p>
                    <p>Project end date: {projectData.endDate}</p>
                    <p>Project quantity: {projectData.quantity}</p>
                    <p>Project status: {projectData.status}</p>
                    <p>Project needed Ability: {projectData.abilityList}</p>
                    <button onClick={handleDeleteProject}>Delete Project</button>
                    <Link to="/projects">
                        <button>Back to Project Page</button>
                    </Link>
                </section>
            ) : (
                <p>No project details found.</p>
            )}
        </div>
    );
};

export default ProjectDetails;
